CREATE TABLE public."Livro" (
	"Codl" int4 GENERATED BY DEFAULT AS IDENTITY( INCREMENT BY 1 MINVALUE 1 MAXVALUE 2147483647 START 1 CACHE 1 NO CYCLE) NOT NULL,
	"Titulo" varchar(40) NOT NULL,
	"Editora" varchar(40) NOT NULL,
	"Edicao" int4 NOT NULL,
	"AnoPublicacao" bpchar(4) NOT NULL,
	CONSTRAINT "PK_Livro" PRIMARY KEY ("Codl")
);